INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`, `lms_id`) VALUES ('2', '1', '2013-08-09 17:04:07', '2013-08-09 17:04:07', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://demo.code5fixer.com/2013/rails/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2013-08-15 19:51:32', '2013-08-15 19:51:32', '', '0', 'http://192.168.1.86/rails/?page_id=2', '0', 'page', '', '0', '');
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`, `lms_id`) VALUES ('44', '1', '2013-08-16 17:23:11', '2013-08-16 17:23:11', '<div class=\"row_black_wrapper overflow_fix \">\r\n<div class=\"row_inner about_compelling\">\r\n<h1 class=\"about_compelling\">About The Compelling Image</h1>\r\n<h2 class=\"about_compelling\">Welcome to The Compelling Image. We\'re an online and interactive school of photography and multimedia storytelling - all with an eye to visual expression in the digital age.</h2>\r\n<div class=\"txt_para\">\r\n<div class=\"learn_online\">\r\n<h3 class=\"about_compelling\">Learn online anywhere - anytime...</h3>\r\n<p class=\"about_compelling\">All of our courses take place in the internet. You won\'t need to schedule vacation time and travel long and expensive distances to enjoy and benefit from high-quality, interactive professional photography, multimedia and video instruction. All you need is a computer and a connection to the internet.</p>\r\n\r\n<h3 class=\"about_compelling\">Getting started with a photography course</h3>\r\n<p class=\"about_compelling\">Just look through our exciting list of courses and portfolio reviews, read about our accomplished faculty of international instructors and click to enroll your choices. Using a major credit card or PayPal, you\'ll be guided through our secure payment process - that\'s it!</p>\r\n\r\n</div>\r\n<div class=\"video\"><iframe src=\"//www.youtube.com/embed/B2QELumVfpc\" height=\"259\" width=\"479\" allowfullscreen=\"\" frameborder=\"0\"></iframe></div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"row_black_wrapper overflow_fix\" style=\"height: 395px;\">\r\n<div class=\"row_inner clearfix grey_border\">\r\n<h1 class=\"about_compelling\">A \"virtual classroom\" experience</h1>\r\n<p class=\"about_compelling\" style=\"text-align: center;\">From your Student Area Dashboard, you\'ll be able to view analysis and constructive critiques of the assignment work you\'ve submitted, as well as receive timely answers to questions posed to your instructor. You\'ll also be able to carry on lively discussions within your \"classroom\" - exchanging feedback and comments with others on your course. You can even schedule a Skype consultation with your instructor to discuss your progress.</p>\r\n<p class=\"txt_white\" style=\"text-align: center;\">As an enrolled TCI student you\'ll receive</p>\r\n\r\n<ul class=\"virtual_class clearfix\">\r\n	<li><a class=\"thumb\" id=\"lessons\"></a><span class=\"whitetxt_small\">Weekly Lessons</span></li>\r\n	<li><a class=\"thumb\" id=\"assignments\"></a><span class=\"whitetxt_small\">Weekly Assignments</span></li>\r\n	<li><a class=\"thumb\" id=\"critiques\"></a><span class=\"whitetxt_small\">Weekly Critiques of\r\nYour Work</span></li>\r\n	<li><a class=\"thumb\" id=\"moneyback\"></a><span class=\"whitetxt_small\">Money Back Guarantee</span></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<div class=\"row_black_wrapper overflow_fix\">\r\n<div class=\"row_inner overflow_fix grey_border\">\r\n<p class=\"title_red txt_center\">Our half-bearded, mostly human team</p>\r\n\r\n<ul class=\"instructor_list clearfix\" id=\"instructor_team\">\r\n	<li>Owner\r\n<h2>John Smith</h2>\r\n<img alt=\"John smith\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/instruc1.png\" />\r\n<div class=\"share\"></div></li>\r\n	<li>Operations\r\n<h2>Monica</h2>\r\n<img alt=\"Monica\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/instruc2.png\" />\r\n<div class=\"share\"></div></li>\r\n	<li>Designer\r\n<h2>Abiraham</h2>\r\n<img alt=\"Abiraham\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/instruc3.png\" />\r\n<div class=\"share\"></div></li>\r\n	<li>Designer\r\n<h2>Linda Mathew</h2>\r\n<img alt=\"Linda Mathew\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/instruc4.png\" />\r\n<div class=\"share\"></div></li>\r\n	<li>Designer\r\n<h2>James</h2>\r\n<img alt=\"James\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/instruc5.png\" />\r\n<div class=\"share\"></div></li>\r\n	<li><img style=\"margin-top: 15px;\" alt=\"John smith\" src=\"http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images/img_instructyou.jpg\" /></li>\r\n</ul>\r\n</div>\r\n</div>', 'About Us', '', 'publish', 'open', 'open', '', 'about-us', '', '', '2013-08-22 06:55:46', '2013-08-22 06:55:46', '', '0', 'http://192.168.1.86/rails/?page_id=44', '0', 'page', '', '0', '');
