INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`, `lms_id`) VALUES ('16', '1', '2013-08-09 17:34:40', '2013-08-09 17:34:40', '<p>\r\nGot a hankering to shoot dynamic photos of the great outdoors and those who play hard in it? This 4-week online photography course taught by professional adventure photographer, Dan Bailey, will guide you directly there.<br>\r\nYou love the outdoors and everything that has to do with being active in it. And part of this enjoyment has to do with capturing in pictures all the action and excitement of moments spent there. Photographing outdoor sports and the whole range of adventure possibilities calls for a special brand of camera craft &ndash; one that doesnt interfere with the process, but brings to life over-and-over again, all the fun and excitement of having been there. This action- and expertise-packed online photography course will gear you up to meet the challenge of all that lies to photograph in the great outdoors.\r\n</p>\r\n<p><strong>What you will need:</strong></p>\r\n<ul class=\"inner\">\r\n<li>Digital SLR or compact mirrorless camera (film is also fine, but youll need to scan http://demo.code5fixer.com/2013/rails/wp-content/themes/thecompellingimage/source/images for upload to the TCI website)</li>\r\n<li>Selection of lenses ranging from wide-angle to telephoto</li>\r\n<li>Computer with photo-processing software (e.g. Photoshop)</li>\r\n<li>A light but study tripod would be beneficial</li>\r\n</ul>\r\n<p><strong>What you will need:</strong></p>\r\n<ul class=\"inner\">\r\n<li>The Quality of Light</li>\r\n<li>Photographing Landscapes</li>\r\n<li>Capturing People in the Natural World</li>\r\n<li>Shooting More Creatively</li>\r\n</ul>\r\n<p><strong>Enroll on this Course:</strong></p>\r\n<p>The next session of this course starts on 08 July 2013 &ndash; there are also other sessions to choose from to ensure you find the course that is most convenient for you.</p>\r\n<p>\r\nTo buy a place on this course, please select the session that you would like to enroll on:\r\n</p>', 'Adventure Photography', '', 'publish', 'open', 'closed', '', 'adventure-photography', '', '', '2013-08-17 18:11:58', '2013-08-17 18:11:58', '', '0', 'http://192.168.1.86/rails/?post_type=product&#038;p=16', '0', 'product', '', '0', '');
